mapper.select:
select * from IncrOrder

	and ChangeTime > #{lastTime} 
	and IncrID > #{lastId} 
	and OrderFrom =#{orderFrom} 
	and ProxyId =#{proxyId} 

ORDER BY IncrID LIMIT 0,1

